import React, { Component } from "react";
import "./BaiTapBookingTicket.css";
export default class BookingCinemaTicket extends Component {
  render() {
    return (
      <div
        className="bookingMovie"
        style={{
          position: "fixed",
          width: "100%",
          height: "100%",
          backgroundImage: "url(img/bgmovie.jpg)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
          }}
        >
          <div className="container-fluid">
            <div className="row">
              <div className="col-8">
                <div className="text-warning display-4 text-center text-uppercase">
                  Bài Tập Đặt Vé Xem Phim
                </div>

                <div
                  className="text-light mt-2 text-center"
                  style={{ fontSize: "30px" }}
                >
                  Màn Hình
                </div>
                <div className="d-flex justify-content-center row mt-2">
                  <div className="screen"></div>
                </div>
              </div>
              <div className="col-4 text-center">
                <div
                  className="text-warning text-uppercase"
                  style={{ fontSize: "50px" }}
                >
                  Danh sách ghê bạn chọn
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
