import React, { Component } from "react";
import "./BaiTapBookingTicket.css";

import ThongTinDatGhe from "./ThongTinDatGhe";
export default class BookingCinemaTicket extends Component {
  renderHangGhe = () => {
    return;
  };
  render() {
    return (
      <div
        className="bookingMovie"
        style={{
          position: "fixed",
          width: "100%",
          height: "100%",
          backgroundImage: "url(img/bgmovie.jpg)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
          }}
        >
          <div className="container-fluid">
            <div className="row">
              <div className="col-8">
                <div className="text-warning display-4 text-center text-uppercase mt-2">
                  Booking Movie Tickets
                </div>

                <div
                  className="text-light mt-2 text-center"
                  style={{ fontSize: "30px" }}
                >
                  Screen
                </div>
                <div className="d-flex justify-content-center row mt-2">
                  <div className="screen"></div>
                  {this.renderHangGhe()}
                </div>
              </div>
              <div className="col-4 mt-2">
                <div
                  className="text-light text-uppercase"
                  style={{ fontSize: "35px" }}
                >
                  Seat Availability
                </div>
                <ThongTinDatGhe />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
