import React, { Component } from "react";

export default class HangGhe extends Component {
  renderGhe = () => {
    return this.props.hangGhe.danhSachGhe.map((ghe, index) => {
      let cssBooked = "";
      let disabled = false;
      if (ghe.daDat) {
        cssBooked = "gheDuocChon";
        disabled = true;
      }
      return (
        <button
          key={index}
          className={`ghe ${cssBooked}`}
          onClick={() => {
            alert("Seat was booked");
          }}
        >
          {ghe.soGhe}
        </button>
      );
    });
  };
  render() {
    return (
      <div
        className="text-light text-left ml-5 mt-5"
        style={{ fontSize: "25px" }}
      >
        {this.props.hangGhe.hang} {this.renderGhe()}
      </div>
    );
  }
}
